import './index.css'
import {Component} from 'react'
import TeamCard from '../TeamCard/index'

class Home extends Component {
  state = {teamsData: []}

  componentDidMount() {
    this.getAllTeams()
  }

  getAllTeams = async () => {
    const response = await fetch('https://apis.ccbp.in/ipl')
    const data = await response.json()
    const {teams} = data

    const updatedTeamData = teams.map(eachTeam => ({
      id: eachTeam.id,
      name: eachTeam.name,
      teamImageUrl: eachTeam.team_image_url,
    }))

    this.setState({teamsData: updatedTeamData})
  }

  render() {
    const {teamsData} = this.state

    return (
      <div className="page-bg">
        <div className="logo-container">
          <img
            src="https://assets.ccbp.in/frontend/react-js/ipl-logo-img.png "
            alt="ipl logo"
            className="iplLogo-pro"
          />
          <h1 className="heading-pro">IPL Dashboard</h1>
        </div>
        <div className="teamsCards-holder">
          {teamsData.map(eachTeam => (
            <TeamCard key={eachTeam.id} eachTeam={eachTeam} />
          ))}
        </div>
      </div>
    )
  }
}

export default Home
