import {Component} from 'react'
import './index.css'
import LatestMatch from '../LatestMatch/index'

class TeamMatches extends Component {
  state = {teamDetails: {}}

  componentDidMount() {
    this.getTeamDetails()
  }

  getTeamDetails = async () => {
    const {match} = this.props
    const {params} = match
    const {id} = params

    const response = await fetch(`https://apis.ccbp.in/ipl/${id}`)

    const data = await response.json()

    const updatedData = {
      latestMatchDetails: data.latest_match_details,
      recentMatches: data.recent_matches,
      teamBannerUrl: data.team_banner_url,
    }

    this.setState({teamDetails: updatedData})
  }

  render() {
    const {teamDetails} = this.state
    const {teamBannerUrl} = teamDetails

    return (
      <div className="TeamMatches-pro">
        <div className="teamMatch-bg">
          <div className="image-contianer">
            <img src={teamBannerUrl} alt="teamBanner" className="banner-pro" />
          </div>
        </div>
        <div className="latest-matches-pro">
          <p>Latest Matches</p>
          <div>
            <LatestMatch />
          </div>
        </div>
      </div>
    )
  }
}

export default TeamMatches
